<?  // Taken from builder of UE, directory Builder/Lib/Cache    $Loader->Load_Lib('/FS/Utils');    Function Cache_FileLoad($CacheFile, $Hash, $Getter)  {    if(FS_Is_File($CacheFile))    {      $Res=Include(FS_EnCode($CacheFile));      if($Res['Hash']===$Hash)        return $Res['Data'];    }    $Content=$Getter();    $Res=[      'Hash'=>$Hash    ,      'Data'=>$Content ,    ];    $Res=''      .'<?'."\n"      .'Return'."\n"      .Var_Export($Res, true).";\n"      .'?>'    ;    CreatePath(GetDirPath($CacheFile));    FS_File_Put_Contents($CacheFile, $Res);    return $Content;  }?>